def average_score(scores):
    if not scores:
        return 0.0
    total = sum(scores)
    count = len(scores)
    return total / count


def classify_student(name, scores):
    avg = average_score(scores)
    if avg >= 90:
        grade = "A"
    elif avg >= 75:
        grade = "B"
    elif avg >= 60:
        grade = "C"
    else:
        grade = "D"

    return {
        "name": name,
        "average": round(avg, 2),
        "grade": grade
    }


students = [
    ("Aarav",  [95, 88, 92]),
    ("Riya",   [76, 81, 79]),
    ("Kunal",  [59, 63, 57]),
    ("Meera",  [89, 91, 94])
]


report = []
for name, scores in students:
    info = classify_student(name, scores)
    report.append(info)


for entry in report:
    print(entry["name"], "->" , entry["average"], entry["grade"])
